import { Cart_item } from '../../utils/types';
import CartService from './carts-service';

export async function getCartItems(e) {
  const cart = await CartService.getCartItems(e.pathParameters.userId);
  return { cart: [cart] };
}

export async function addCartItem(e) {
  const bid = e.pathParameters.userId;
  const pbody = JSON.parse(e.body);
  const info: Cart_item = {
    buyer_id: bid,
    product_id: pbody.id,
    quantity: pbody.quantity,
  };
  const cart = await CartService.addCartItem(info);
  return { cart: cart };
}

export async function updateCartItems(e) {
  const bid = e.pathParameters.userId;
  const pbody = JSON.parse(e.body).cart_items;
  const info = pbody.map((e) => `(${bid}, ${e.id}, ${e.quantity})`).join(', ');
  return await CartService.updateCartItems(bid, info);
}

export async function deleteCartItem(e) {
  const bid = e.pathParameters.userId;
  const pid = e.pathParameters.id;
  return await CartService.deleteCartItem(bid, pid);
}

import { SQLConnectionManager } from '/opt/sql-layer';

class CartModel {
  public async getCartItems(bid) {
    const sql = `SELECT JSON_OBJECT(
      'products', (SELECT JSON_ARRAYAGG(JSON_OBJECT("id", p.id, "product_uri", pm.url, "name", p.name, "quantity", c.quantity, "price", p.price)))) as cart
      FROM sqlDB.cart_item c
      INNER JOIN sqlDB.product p ON c.product_id = p.id
      INNER JOIN sqlDB.product_multimedia pm ON p.id = pm.product_id
      WHERE c.buyer_id = ?`;
    return await SQLConnectionManager.queryPool(sql, [bid]);
  }

  public async addCartItem(info) {
    console.log('add cart in model');
    const sql = `INSERT INTO sqlDB.cart_item SET ?`;
    await SQLConnectionManager.queryPool(sql, [info]);
    return await this.confirmCartAdd(info);
  }

  public async findAllCartItems() {
    const sql = 'SELECT * FROM sqlDB.cart_item';
    return await SQLConnectionManager.queryPool(sql);
  }

  public async deleteCartItem(bid, pid) {
    const sql =
      'DELETE FROM sqlDB.cart_item WHERE buyer_id = ? AND product_id = ?';
    return await SQLConnectionManager.queryPool(sql, [bid, pid]);
  }

  public async updateCartItems(bid, info) {
    const sql = `INSERT INTO sqlDB.cart_item (buyer_id, product_id, quantity) VALUES ${info} ON DUPLICATE KEY UPDATE quantity = VALUES(quantity)`;
    await SQLConnectionManager.queryPool(sql);
    return await this.confirmCartUpdate(bid);
  }

  public async confirmCartAdd(info) {
    const sql = `SELECT product_id, quantity FROM sqlDB.cart_item WHERE buyer_id = ? AND product_id = ?`;
    return await SQLConnectionManager.queryPool(sql, [
      info.buyer_id,
      info.product_id,
    ]);
  }

  public async confirmCartUpdate(bid) {
    const sql = `SELECT JSON_ARRAYAGG(JSON_OBJECT("id", buyer_id, "quantity", quantity)) as cart_items
      FROM sqlDB.cart_item
      WHERE buyer_id = ?`;
    return await SQLConnectionManager.queryPool(sql, [bid]);
  }
}

export default new CartModel();

